def find_max(numbers):
  if not numbers:
    return None
  biggest = numbers[0]
  for number in numbers:
    if number > biggest:
      biggest = number
  return biggest

def is_palindrome(s):
  s = s.lower().replace(' ', '')
  return s == s[::-1]

